import React, { Component } from "react";
import { Link } from "react-router-dom";

class About extends Component {
  render() {
    var toko=require('../images/toko.jpeg')
    return (
<div class="colorlib-about">
	<div class="container">
		<div class="row row-pb-lg">
			<div class="col-sm-6 mb-3">
				<div class="video colorlib-video" style={{backgroundImage: "url("+toko+")"}}>
					<div class="overlay"></div>
				</div>
			</div>
			<div class="col-sm-6">
				<div class="about-wrap">
					<h2>Sis Sport the best store around the globe</h2>
					<p>Sis Sport adalah toko online yang menjual sepatu olahraga, seperti sepatu futsal, sepakbola, basket, dan
						lain-lain. Kami menyediakan sepatu dengan kualitas terbaik dan dengan harga yang terjangkau.</p>
					<ul>
						<li>Jam operasional toko 10.00-18.00</li>
						<li>Waktu pengiriman : jne senin-sabtu jam 18.00</li>
						<li>Fast respond : jam 10-00-18.00 sesuai jam operasional</li>
					</ul>
				</div>
			</div>
		</div>
    </div>
</div>
    );
  }
}

export default About;
