import React, { Component } from 'react';
import './assets/css/listticket.css';
import {Link} from 'react-router-dom';
import { withRouter } from 'react-router-dom';
import { connect } from 'unistore/react';
import { actions } from '../store';
import '../styles/App.css';

class IsiTicketList extends Component {

    render() {
        return (
            <div>
                <div className="blog-card">
                    <div className="meta">
                    {this.props.airline == 'Batik Air' && 
                            <div className="photo" style={{backgroundImage: "url(https://3.bp.blogspot.com/-7NjNtsopiyA/Whjb1ZrHaqI/AAAAAAAAFYc/EOa99HoUMgUOK5D3hMPf07kNtsDN8MOywCLcBGAs/s320/batik%2Bair.jpg)"}}></div>
                        }
                        {this.props.airline == 'Citilink' && 
                            <div className="photo" style={{backgroundImage: "url(https://2.bp.blogspot.com/-kcAFN1pdt2g/VgjjMoip7wI/AAAAAAAAItU/XOkXvNtFe1c/s1600/logo%2Bmaskapai%2Bpenerbangan%2Bcitilink%2Bindonesia.JPG)"}}></div>
                        }
                        {this.props.airline == 'Garuda Indonesia' && 
                            <div className="photo" style={{backgroundImage: "url(https://vignette.wikia.nocookie.net/logopedia/images/d/d2/Logo_Garuda_Indonesia.jpg/revision/latest?cb=20150813112548)"}}></div>
                        }
                        {this.props.airline == 'Lion Air' && 
                            <div className="photo" style={{backgroundImage: "url(https://s3-ap-southeast-1.amazonaws.com/airy-marketing/pictures/logo+lion+air.jpg)"}}></div>
                        }
                        {this.props.airline == 'Sriwijaya Air' && 
                            <div className="photo" style={{backgroundImage: "url(https://2.bp.blogspot.com/-FI54eTh42J0/UPp1iZvSqHI/AAAAAAAAFPE/Y4X5ZsDtbm0/s1600/Logo+Sriwijaya+Air.jpg)"}}></div>
                        }
                        {((this.props.airline != 'Sriwijaya Air') &&  (this.props.airline != 'Batik Air') && (this.props.airline != 'Citilink') && (this.props.airline != 'Garuda Indonesia') && (this.props.airline != 'Lion Air')) &&
                        <div className="photo" style={{backgroundImage: "url(https://s.kaskus.id/images/fjb/2018/01/16/_1377773_1516071573.gif)"}}></div> }

                    <ul className="details">
                        <li className="author" onClick={(e) => this.props.getWeather()}><Link to='/sayonara'>Book</Link></li>
                        <li className="date">{ this.props.waktu_asal.slice(11, 19) } -> { this.props.waktu_tujuan.slice(11, 19) }</li>
                        <li className="tags">

                        </li>
                    </ul>
                    </div>
                    <div className="description">
                    <h1>{ this.props.airline }</h1>
                    <h2> { this.props.departureIataCode } -> { this.props.arrivalIataCode }</h2>
                    {this.props.airline == 'Batik Air' && 
                            <p>Estimasi Harga: Rp.{ this.props.estimasi_harga_batik },-</p>
                        }
                        {this.props.airline == 'Citilink' && 
                            <p>Estimasi Harga: Rp.{ this.props.estimasi_harga_citilink },-</p> 
                        }
                        {this.props.airline == 'Garuda Indonesia' && 
                            <p>Estimasi Harga: Rp.{ this.props.estimasi_harga_garuda },-</p> 
                        }
                        {this.props.airline == 'Lion Air' && 
                            <p>Estimasi Harga: Rp.{ this.props.estimasi_harga_lion },-</p> 
                        }
                        {this.props.airline == 'Sriwijaya Air' && 
                            <p>Estimasi Harga: Rp.{ this.props.estimasi_harga_sriwijaya },-</p> 
                        }
                        {((this.props.airline != 'Sriwijaya Air') &&  (this.props.airline != 'Batik Air') && (this.props.airline != 'Citilink') && (this.props.airline != 'Garuda Indonesia') && (this.props.airline != 'Lion Air')) &&
                        <p>Estimasi Harga: Rp.{ this.props.estimasi_harga_lain },-</p> }
                    </div>
                    </div>            
                </div>
            );
    }
}

export default connect(
    'is_login',
    actions
)(withRouter(IsiTicketList));
