import React, { Component } from 'react';
import loadjs from 'loadjs'
import Footer from '../components/Footer'
import BannerAll from '../components/BannerAll'
import Partner from '../components/Partner'
import ContentCategory from '../components/ContentCategory'
import {connect} from 'unistore/react'
import {actions} from '../store';
import {withRouter} from 'react-router-dom'
class Category extends Component {
  componentDidMount = async () => {
    this.props.getAllProduct()
	}
  componentWillMount() {
    loadjs(["js/jquery.min.js", 
    "../js/popper.min.js", 
    "../js/bootsrap.min.js", 
    "../js/jquery.easing.1.3.js", 
    "../js/jquery.waypoints.min.js",
    "../js/jquery.flexslider-min.js",
    "../js/owl.carousel.min.js",
    "../js/jquery.magnific-popup.min.js",
    "../js/magnific-popup-options.js",
    "../js/bootstrap-datepicker.js",
    "../js/jquery.stellar.min.js",
    "../js/main.js",
  ], function() {
    });
  }

  correctList = all_product => {
    return all_product.category === this.props.category;
  };

  render() {
    const filtered = this.props.all_product.filter(this.correctList);
    console.log("aaa", filtered)
    return (
      <div>
        <BannerAll/>
        <div class="colorlib-product">
			<div class="container">
				<div class="row">
					<div class="col-sm-8 offset-sm-2 text-center colorlib-heading colorlib-heading-sm">
						<h2>View All Products</h2>
					</div>
				</div>
				<div class="row row-pb-md">
        {filtered.map((item, key) => {
              return (
          <ContentCategory key={key} imgurl1={item.imgurl1} item_name={item.item_name} price={item.price}/>
          )
              })}
        </div>
				<div class="row">
					<div class="col-md-12 text-center">
						<div class="block-27">
							<ul>
								<li><a href="#"><i class="ion-ios-arrow-back"></i></a></li>
								<li class="active"><span>1</span></li>
								<li><a href="#">2</a></li>
								<li><a href="#">3</a></li>
								<li><a href="#">4</a></li>
								<li><a href="#">5</a></li>
								<li><a href="#"><i class="ion-ios-arrow-forward"></i></a></li>
							</ul>
						</div>
					</div>
				</div>
			</div>
		</div>
        <Partner/>
        <Footer/>
      </div>
    );
  }
}

export default connect(
	"all_product",
	actions
)(withRouter(Category));
