import React, { Component } from "react";
import axios from "axios";
import { withRouter } from "react-router-dom";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = { username: "", password: "" };
  }

  changeInput = event => {
    this.setState({ [event.target.name]: event.target.value });
  };
  postLogin = () => {
    const { username, password } = this.state;
    const data = {
      username: username,
      password: password
    };
    const self = this;
    axios
      .post("https://api-todofancy.herokuapp.com/api/auth", data)
      .then(function(response) {
        localStorage.setItem("api_key", response.data.api_key);
        localStorage.setItem("is_login", true);
        localStorage.setItem("username", response.data.user_data.username);
        localStorage.setItem("email", response.data.user_data.email);
        localStorage.setItem("avatar", response.data.user_data.avatar);
        self.props.history.push("/home");
      })
      .catch(function(error) {
        console.log(error);
      });
  };
  render() {
    return (
      <section>
        <div class="wrapper1 fadeInDown">
          <div id="formContent">
            <div class="fadeIn first">
              <img
                src={require("../static/img/teleflight (2).png")}
                id="icon"
                alt="User Icon"
              />
            </div>

            <form onSubmit={event => event.preventDefault()}>
              <input
                type="text"
                id="login"
                class="fadeIn second"
                name="login"
                placeholder="username"
                onChange={event => this.changeInput(event)}
              />
              <input
                type="password"
                id="password"
                class="fadeIn third"
                name="login"
                placeholder="password"
              />
              <input
                type="submit"
                class="fadeIn fourth"
                value="Sign In"
                onClick={() => this.postLogin()}
              />
            </form>
          </div>
        </div>
      </section>
    );
  }
}

export default withRouter(Login);
