import React, { Component } from "react";
import axios from 'axios'
import { withRouter } from "react-router-dom";
const url = "https://loszapatos.web.id/"
const url_cors = "https://cors-anywhere.herokuapp.com/";

class LoginFormBuyer extends Component {
	state = { username: "", password: "" };
  changeInput = e => {
    this.setState({[e.target.name]: e.target.value});
  };
  postLogin = async () => {
    const {username, password} = this.state;
    const header =  {headers:{"Origin": "https://loszapatos.web.id/", "Content-Type": "application/json"}}
    const data = {
      username:username,
      password:password
    };
    const self = this;
    await axios
    .post(url_cors + url + "login/buyer", data, header)
    .then(function(response){
      if (response.data.hasOwnProperty("token")) {
        localStorage.setItem("token", response.data.token);
        localStorage.setItem("isLogin", true);
        self.props.history.push("/loggedin")
      }
    })
    .catch(function(error){
      console.log(error);
    });
  };

  render() {
    return (
		<div class="container">
			<div class="col-md-6">
				<div class="form-group">
					<label for="companyname">Name</label>
					<input type="text" id="name" name="username" class="form-control" placeholder="Username" onChange={e => this.changeInput(e)}/>
				</div>
			</div>
			<div class="col-md-6">
				<div class="form-group">
					<label for="email">Password</label>
					<input type="password" id="password" name="password" class="form-control" placeholder="Password" onChange={e => this.changeInput(e)}/>
				</div>
			</div>
			<div class="col-md-6">
				<button class="buttonedit buttonedit1" type="submit" onClick={() => this.postLogin()}>Log In</button>
			</div>
		</div>
        )
    }
}

export default withRouter(LoginFormBuyer);
