import createStore from 'unistore';
import axios from 'axios';

const initialState = {
    api_key: "",
    username: "",
    password: "",
    full_name: "",
    email: "",
    is_login: false,
    listNews: [],
    listTopNews: [],
    search: "",
    iata_asal: "SUB",
    airport_id_asal: "",
