    getWeather : async state => {
        try {
        const response = await axios.get(
        "http://api.weatherbit.io/v2.0/current?lat=-7.2575&lon=112.7521&key=aa3c976285c64dafa3662f2e99d4bd03"
        )
        store.setState({weather: response.data.data[0].weather.description,
        temperature: response.data.data[0].temp,
        sunrise: response.data.data[0].sunrise,
        sunset: response.data.data[0].sunset,
        })
        }
        catch(error){
        console.log(error)
        }
        },

    getRates : async state => {
        try {
        const response = await axios.get(
        " https://api.exchangeratesapi.io/latest?base=IDR"
        )
        store.setState({weather: response.data.rates.USD})
        }
        catch(error){
        console.log(error)
        }
        },

    rad : (state, x) => {
        return x * Math.PI / 180;
        },

    getRange : (state, lat_asal, long_asal, lat_tujuan, long_tujuan) => {
        // const rad = function(x) {
        //     return x * Math.PI / 180;
        //     };

        const radius_bumi = 6378137; // Earth’s mean radius in meter
        const latitude = rad(lat_asal - lat_tujuan); // latitude awal - latitude akhir
        const longitude = rad(long_asal - long_tujuan); // longitude awal - longitude akhir
        const a = Math.sin(latitude / 2) * Math.sin(latitude / 2) + Math.cos(rad(lat_tujuan)) * Math.cos(rad(lat_asal)) * Math.sin(longitude / 2) * Math.sin(longitude / 2);
        const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
        const distance_in_km = (radius_bumi * c) / 1000;
        const distance = Math.round(distance_in_km);
        store.setState({range: distance})
    },

    // getData: async state => {
