import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios"
import Transactions from './Transactions'
import {Redirect} from 'react-router-dom'
import { withRouter } from "react-router-dom";
import {connect} from 'unistore/react'
import {actions} from '../store';

const isLogin = JSON.parse(localStorage.getItem("isLogin"));
const token = localStorage.getItem("token");
const url = "https://loszapatos.web.id/";
const url_cors = "https://cors-anywhere.herokuapp.com/";

class BuyerData extends Component {

 componentDidMount = async () => {
    this.props.getBuyerData()
};

render() {
    if (isLogin === false) {
        return <Redirect to={{pathname: "/logorsign"}}/>;
      }
      else {
    return (
        <div class="container">
            <div class="row">
                <div class="col-sm-8 offset-sm-2 text-center colorlib-heading">
                    <h2>{this.state.fullname}</h2>
                </div>
            </div>
            <div class="container">
                <div class="row">
                    <div class="col-sm-6 text-center">
                        <img src={this.state.url_image} class="img-fluid" alt="Free html5 bootstrap 4 template"/>
                    </div>
                    <div class="col-sm-6 text-center">
                        <table class="table table-sm">
                            <thead>
                                <tr>
                                    <th scope="col">Field</th>
                                    <th scope="col">Data</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td>Username</td>
                                    <td>{this.state.username}</td>
                                </tr>
                                <tr>
                                    <td>Address</td>
                                    <td>{this.state.address}</td>
                                </tr>
                                <tr>
                                    <td>Phone</td>
                                    <td>{this.state.phone}</td>
                                </tr>
                                <tr>
                                    <td>Email</td>
                                    <td>{this.state.email}</td>
                                </tr>
                            </tbody>
                        </table>
                        <div>
                        <Link to="/buyerprofileedit"><button class="buttonedit buttonedit1">Edit</button></Link>
                        </div><br/>
                        <Transactions/>
                    </div>             
                </div>
            </div>
        </div>
    );
  }
}
}
export default connect(
	"isLogin",
	actions
)(withRouter(BuyerData));
