import createStore from 'unistore';
import axios from 'axios';

const initialState = {
    api_key: "",
    username: "",
    password: "",
    full_name: "",
    email: "",
    is_login: false,
    iata_asal: "",
    airport_id_asal: "",
    airport_name_asal: "",
    nation_code_asal: "",
    latitude_asal: "",
    longitude_asal: "",
    iata_tujuan: "",
    airport_id_tujuan: "",
    airport_name_tujuan: "",
    nation_code_tujuan: "",
    latitude_tujuan: "",
    longitude_tujuan: "",
    flight_list: [],
    weather: "",
    code: "",
    weather_code: "",
    temperature: "",
    sunrise: "",
    sunset: "",
    rate: "",
    base_currency:"",
    target_currency:"",
    new_sum:"",
    base_sum: "",
    word: "",
    distance_in_km: ""
};

export const store = createStore(initialState);

export const actions = store => ({
    setField: (state, event) => {
        return { [event.target.name]: event.target.value };
    },

    postLogout: state => {
        return { is_login: false };
    },

    postLogin: async state => {
        const data = { username: state.username, password: state.password };
        await axios
            .post("https://ilfarro1.free.beeceptor.com/signin", data)
            .then(response => {
                console.log('post loginnnnnn!!!!', response.data)
                if (response.data.hasOwnProperty('api_key')) {
                    store.setState({
                        'api_key': response.data.api_key,
                        'is_login': true,
                        full_name: state.username,
                        'email': response.data.email
                    })
                }
            })
            .catch(function(error) {
                console.log(error);
            })
    },

    setAsal: (state, e) => {
        store.setState({ iata_asal: e.target.value })
    },

    setTujuan: (state, e) => {
        store.setState({ iata_tujuan: e.target.value })
    },

    setCurrAsal : (state, e )=> {
        store.setState({base_currency : e.target.value})
    },

    setCurrTujuan : (state, e )=> {
        store.setState({target_currency : e.target.value})
    },

    getPrice : (state, lat_asal, long_asal, lat_tujuan, long_tujuan) => {
        const rad = function(x) {
            return x * Math.PI / 180;
            };

        const radius_bumi = 6378137; // Earth’s mean radius in meter
        const latitude = rad(lat_asal - lat_tujuan); // latitude awal - latitude akhir
        const longitude = rad(long_asal - long_tujuan); // longitude awal - longitude akhir
        const a = Math.sin(latitude / 2) * Math.sin(latitude / 2) + Math.cos(rad(lat_tujuan)) * Math.cos(rad(lat_asal)) * Math.sin(longitude / 2) * Math.sin(longitude / 2);
        const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
        const distance_in_km = (radius_bumi * c) / 1000;
        store.setState({distance_in_km: distance_in_km})
    },

    getNewCurrency: async (state, moneysum) => {
        await axios
        .get("https://api.exchangeratesapi.io/latest?base=" + state.base_currency)
        .then(function (response) {
            store.setState({rate: response.data.rates[state.target_currency]})
            const new_value = state.rate * moneysum
            store.setState({new_sum: new_value})
        })
        .catch(function(error) {
           console.log(error); 
        });
    },

    getWeather: async state => {
        await axios
        .get("http://api.weatherbit.io/v2.0/current?lat=" + state.latitude_tujuan + "&lon=" + state.longitude_tujuan + "&key=aa3c976285c64dafa3662f2e99d4bd03")
        .then(function (response) {
            store.setState({
                weather: response.data.data[0].weather.description,
                temperature: response.data.data[0].temp,
                sunrise: response.data.data[0].sunrise,
                sunset: response.data.data[0].sunset,
                weather_code: response.data.data[0].weather.code
                });
        })
        .catch(function(error) {
           console.log(error); 
        });
    },

    getData: async state => {
        await axios
        .all([
        axios.get("https://aviation-edge.com/v2/public/airportDatabase?key=eb2b4a-342a5e&codeIataAirport=" + state.iata_asal),
        axios.get("https://aviation-edge.com/v2/public/airportDatabase?key=eb2b4a-342a5e&codeIataAirport=" + state.iata_tujuan),
        axios.get("http://aviation-edge.com/v2/public/timetable?key=eb2b4a-342a5e&type=departure&status=scheduled&iataCode=" + state.iata_asal),
        axios.get("https://api.exchangeratesapi.io/latest?base=" + state.base_currency),
        ])
        .then(
        axios.spread(function(response1, response2, response3, response4) {
        store.setState({ 
            airport_id_asal: response1.data[0].airportId,
            airport_name_asal: response1.data[0].nameAirport,
            nation_code_asal: response1.data[0].codeIso2Country,
            latitude_asal: response1.data[0].latitudeAirport,
            longitude_asal: response1.data[0].longitudeAirport
        });
        store.setState({ 
            airport_id_tujuan: response2.data[0].airportId,
            airport_name_tujuan: response2.data[0].nameAirport,
            nation_code_tujuan: response2.data[0].codeIso2Country,
            latitude_tujuan: response2.data[0].latitudeAirport,
            longitude_tujuan: response2.data[0].longitudeAirport
        });
        store.setState({ flight_list: response3.data });
        store.setState({rate: response4.data.rates.USD});
        })
        )
        .catch(function(error) {});
        },

})
