import React, { Component } from "react";
import { Link } from "react-router-dom";
import { withRouter } from "react-router-dom";
import {connect} from 'unistore/react'
import {actions} from '../store';
const ContentCategory = props => {
    return (		
	<div class="col-md-3 col-lg-3 mb-4 text-center">
		<div class="product-entry border">
			<Link to="/productdetail" class="prod-img" name={props.id} onClick={this.props.doClick}>
				<img src={props.imgurl1} class="img-fluid" name={props.id} alt="Free html5 bootstrap 4 template"/>
			</Link>
			<div class="desc">
				<h2><Link to="/productdetail" name={props.id} onClick={this.props.doClick}>{props.item_name}</Link></h2>
				<span class="price">Rp.{props.price}</span>
			</div>
		</div>
	</div>				
    );
  }

	export default connect(
		"isLogin",
		actions
	)(withRouter(ContentCategory));
