import React, { Component } from 'react';
// import axios from 'axios';
import { Redirect, withRouter } from 'react-router-dom';
import { connect } from 'unistore/react';
import { actions } from '../store';
import '../styles/App.css';
import Search from '../components/Search';
import OneRow from '../components/OneRow';
import Kategori from '../components/Kategori';
import ListFlight from '../components/ListFlight';


class Home extends Component {

    componentDidMount() {
        // this.props.getNews();
        // this.props.airportDetail()
        // this.props.getWeather()
        // this.props.getRates()
        this.props.getData()
    }

    handleChange = (e) => {
        this.props.setField(e)
        this.props.doSearch(e.target.value);
    }

    kategori = (e) => {
        this.props.doSearch(e.target.textContent);
    }

    estimasi_harga_lion = (e) =>{ 
        this.props.getPrice(this.props.latitude_asal, this.props.longitude_asal, this.props.latitude_tujuan, this.props.longitude_tujuan)
        const price = Math.round(this.props.distance_in_km * 1271.49321267)
        return price
    }
        // const estimasi_harga_citilink = Math.round(distance_in_km * 1395.173453997)
        // const estimasi_harga_batik = Math.round(distance_in_km * 1586.726998492)
        // const estimasi_harga_sriwijaya = Math.round(distance_in_km * 1736.04826546)
        // const estimasi_harga_garuda = Math.round(distance_in_km * 2419.306184012)
        // const estimasi_harga_lain = Math.round(distance_in_km * 2000)
    // getFinalPrice = (e) => {
    //     // this.props.getRange()
    //     const price = this.props.getPrice(this.props.latitude_asal, this.props.longitude_asal, this.props.latitude_tujuan, this.props.longitude_tujuan);
    //     return price
    // }

    moneyExchange = (e) => {
        // this.props.getRange()
        this.props.getNewCurrency(800000, "JPY");
    }

    correctList = (flight_list) => {
        return flight_list.arrival.iataCode === this.props.iata_tujuan
    }

    render() {
        const filtered = this.props.flight_list.filter(this.correctList)
        // if (this.props.is_login === false) {
        //     return <Redirect to = {{ pathname: '/signin' }} />;
        // } else {
            return (
                <div>
                    <h1>{ this.props.airport_id_asal }</h1>
                    <h1>{ this.props.airport_name_asal }</h1>
                    <h1>{ this.props.nation_code_asal }</h1>
                    <h1>{ this.props.latitude_asal }</h1>
                    <h1>{ this.props.longitude_asal }</h1>
                    <h1>{ this.props.airport_id_tujuan }</h1>
                    <h1>{ this.props.airport_name_tujuan }</h1>
                    <h1>{ this.props.nation_code_tujuan }</h1>
                    <h1>{ this.props.latitude_tujuan }</h1>
                    <h1>{ this.props.longitude_tujuan }</h1>
                    <h1>{ this.props.longitude_tujuan }</h1>
                    <h1>{ this.props.word }</h1>
                    <h1>{ this.props.weather }</h1>
                    <h1>{ this.estimasi_harga_lion() }</h1> 
                    {/* <button onClick={(e) => this.getFinalPrice(e)}>klik</button><br/> */}
                    {/* <button onClick={(e) => this.moneyExchange(e)}>money</button>
                    <h1>{ this.props.new_sum }</h1> */}
                    {/* <h1>{ this.props.range }</h1> */}
                    {/* <button onClick={(e) => this.moneyExchange(e)}>SUB</button>
                    <button onClick={(e) => this.moneyExchange(e)}>JKT</button> */}
                    {filtered.map((item, key) => {
                        return <ListFlight departureIataCode = {item.departure.iataCode} arrivalIataCode = { item.arrival.iataCode } airline = { item.airline.name }/>
                    })}
                    <Search handleChange = { this.handleChange }/>
                    <Kategori kategori = {this.kategori} />
                    <OneRow listNews = {this.props.listNews} listTopNews = {this.props.listTopNews} />
                </div>
            );
        // }
    }
}

export default connect(
    'is_login,listNews,listTopNews,airport_id_asal,airport_name_asal,nation_code_asal,latitude_asal,longitude_asal,airport_id_tujuan,airport_name_tujuan,nation_code_tujuan,latitude_tujuan,longitude_tujuan,iata_tujuan,range,weather,rate,new_sum,flight_list, word, distance_in_km',
    actions
)(withRouter(Home));
