import createStore from "unistore";
import axios from "axios";

const url = "https://loszapatos.web.id/";
const url_cors = "https://cors-anywhere.herokuapp.com/";

const initialState = {
 all_product : [],
 username: "",
 password: "",
 token: "",
 isLogin: false,
 buyer_fullname:"",
 buyer_username:"",
 buyer_email: "",
 buyer_address:"",
 buyer_url_image: "",
 buyer_phone: "",
 user_store_name:"",
 user_email: "",
 user_address:"",
 user_url_image: "",
 user_phone: "",
 user_website: "",
 user_description:"",
};

export const store = createStore(initialState);

export const actions = store => ({
  setField: (state, e) => {
    return { [e.target.name]: e.target.value };
  },

  postLogout: state => {
    store.setState ({ is_login: false, token: "" });
  },

  postLoginBuyer: async state => {
    const header =  {headers:{"Origin": "https://loszapatos.web.id/", "Content-Type": "application/json"}}
    const data = {
      username:state.username,
      password:state.password
    };
    await axios
    .post(url_cors + url + "login/buyer", data, header)
    .then(function(response){
      if (response.data.hasOwnProperty("token")) {
        console.log("bangsat", response.data)
        store.setState({token: response.data.token,
          isLogin: true
        });
      }
    })
    .catch(function(error){
      console.log(error);
    })
  },

  postLoginUser: async state => {
    const header =  {headers:{"Origin": "https://loszapatos.web.id/", "Content-Type": "application/json"}}
    const data = {
      store_name:state.username,
      password:state.password
    };
    await axios
    .post(url_cors + url + "login/user", data, header)
    .then(function(response){
      if (response.data.hasOwnProperty("token")) {
        store.setState({token: response.data.token,
          isLogin: true
        });
      }
    })
    .catch(function(error){
      console.log(error);
    })
  },

  setAsal: (state, e) => {
    store.setState({ iata_asal: e.target.value });
  },

  setTujuan: (state, e) => {
    store.setState({ iata_tujuan: e.target.value });
  },

  setCurrAsal: (state, e) => {
    store.setState({ base_currency: e.target.value });
  },

  setCurrTujuan: (state, e) => {
    store.setState({ target_currency: e.target.value });
  },

  getAllProduct: async state => {
    await axios
      .get(url_cors + url + "item")
      .then(function(response) {
        store.setState({all_product: response.data.data});
      })
      .catch(function(error) {
        console.log(error);
      });
  },

  getBuyerData: async state => {
    const header =  {headers:{"Origin": "https://loszapatos.web.id/", "Authorization": "Bearer " + state.token}}
        await axios
        .get(url_cors + url + "buyer_profile", header)
        .then(function(response){
         store.setState({buyer_fullname:response.data.data.fullname,
                buyer_username:response.data.data.username,
                buyer_email:response.data.data.email,
                buyer_address:response.data.data.address,
                buyer_phone:response.data.data.phone,
                buyer_url_image:response.data.data.url_image,
        })
          }
        )
  },

  getUserData: async state => {
    const header =  {headers:{"Origin": "https://loszapatos.web.id/", "Authorization": "Bearer " + state.token}}
        await axios
        .get(url_cors + url + "user_profile", header)
        .then(function(response){
         store.setState({user_store_name:response.data.data.store_name,
                buyer_username:response.data.data.username,
                user_email:response.data.data.email,
                user_address:response.data.data.address,
                user_phone:response.data.data.phone,
                user_url_image:response.data.data.url_image,
        })
          }
        )
  },


  getData: async state => {
    await axios
      .all([
        axios.get(
          "https://aviation-edge.com/v2/public/airportDatabase?key=57d91e-653b78&codeIataAirport=" +
            state.iata_asal
        ),
        axios.get(
          "https://aviation-edge.com/v2/public/airportDatabase?key=57d91e-653b78&codeIataAirport=" +
            state.iata_tujuan
        ),
        axios.get(
          "http://aviation-edge.com/v2/public/timetable?key=57d91e-653b78&type=departure&status=scheduled&iataCode=" +
            state.iata_asal
        ),
        axios.get(
          "https://api.exchangeratesapi.io/latest?base=" + state.base_currency
        )
      ])
      .then(
        axios.spread(function(response1, response2, response3, response4) {
          store.setState({
            airport_id_asal: response1.data[0].airportId,
            airport_name_asal: response1.data[0].nameAirport,
            nation_code_asal: response1.data[0].codeIso2Country,
            latitude_asal: response1.data[0].latitudeAirport,
            longitude_asal: response1.data[0].longitudeAirport
          });
          store.setState({
            airport_id_tujuan: response2.data[0].airportId,
            airport_name_tujuan: response2.data[0].nameAirport,
            nation_code_tujuan: response2.data[0].codeIso2Country,
            latitude_tujuan: response2.data[0].latitudeAirport,
            longitude_tujuan: response2.data[0].longitudeAirport
          });
          store.setState({ flight_list: response3.data });
          store.setState({ rate: response4.data.rates.USD });
        })
      )
      .catch(function(error) {});
  }
});
