import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios"
import {Redirect} from 'react-router-dom'

const isLogin = JSON.parse(localStorage.getItem("isLogin"));
const token = localStorage.getItem("token");
const url = "https://loszapatos.web.id/";
const url_cors = "https://cors-anywhere.herokuapp.com/";
class DashboardUser extends Component {
    constructor(props){
		super(props);
		this.state={
            store_name:"",
            email: "",
            address:"",
            url_image: "",
            phone: "",
            website: "",
            description:"",
		}
	}
    componentDidMount = async () => {
        const header =  {headers:{"Origin": "https://loszapatos.web.id/", "Authorization": "Bearer " + token}}
		const self = this;
        axios
        .get(url_cors + url + "user_profile", header)
        .then(function(response){
         self.setState({store_name:response.data.data.store_name,
                website:response.data.data.website,
                email:response.data.data.email,
                address:response.data.data.address,
                phone:response.data.data.phone,
                url_image:response.data.data.url_image,
                description:response.data.data.description,
        })
          }
        )};   

  render() {
    if (isLogin === null) {
        return <Redirect to={{pathname: "/logorsign"}}/>;
      }
      else {
    return (
        <div class="container">
            <div class="row">
                <div class="col-sm-8 offset-sm-2 text-center colorlib-heading">
                    <h2>{this.state.store_name}</h2>
                </div>
            </div>
            <div class="container">
                <div class="row">
                    <div class="col-lg-3 col-xl-3">
                        <div class="row">
                            <div class="col-sm-12">
                                <div class="side border mb-1">
                                    <h3>Menu</h3>
                                    <ul>
                                        <li><Link to="/userprofileedit">Edit Profile</Link></li>
                                        <li><Link to="/postitem">Post Product</Link></li>
                                        <li><Link to="/usersitem">Edit Product</Link></li>
                                    </ul>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div class="col-sm-4 text-center">
                        <img src={this.state.url_image} class="img-fluid" alt="Free html5 bootstrap 4 template"/>
                    </div>
                    <div class="col-sm-4 text-center">
                        <table class="table table-sm">
                            <thead>
                                <tr>
                                    <th scope="col">Field</th>
                                    <th scope="col">Data</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td>Address</td>
                                    <td>{this.state.address}</td>
                                </tr>
                                <tr>
                                    <td>Phone</td>
                                    <td>{this.state.phone}</td>
                                </tr>
                                <tr>
                                    <td>Email</td>
                                    <td>{this.state.email}</td>
                                </tr>
                                <tr>
                                    <td>Website</td>
                                    <td>{this.state.website}</td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    );
  }
}
}
export default DashboardUser;
