import React, { Component } from 'react';
import Navbar from './Navbar';
import './assets/css/listticket.css';
import {Link} from 'react-router-dom';
import { withRouter } from 'react-router-dom';
import { connect } from 'unistore/react';
import { actions } from '../store';
import '../styles/App.css';

class Sayonara extends Component {

    render() {
               console.log("aaa", this.props.code)

        return (
            <div>
                <Navbar />
                <div className="card mb-3">
                    {((this.props.code >= 200) && (this.props.code < 300))  && 
                        <img className="card-img-top" src={require('../images/img/hujan-lebat.png')} alt="Card image cap"/>
                    }
                    {((this.props.code >= 300) && (this.props.code < 500)) && 
                        <img className="card-img-top" src={require('../images/img/gerimis.png')} alt="Card image cap"/>
                    }
                    {((this.props.code >= 500) && (this.props.code < 700)) && 
                        <img className="card-img-top" src={require('../images/img/hujan.png')} alt="Card image cap"/>
                    }
                    {this.props.code > 700 && 
                        <img className="card-img-top" src={require('../images/img/cerah.png')} alt="Card image cap"/>
                    }

                    {/* <img className="card-img-top" src={require('./assets/img/background.jpg')} alt="Card image cap"/> */}
                    <div className="card-body">
                    <h5 className="card-title">{ this.props.weather }</h5>
                    <p className="card-text">Panas Bos</p>
                    </div>
                </div>
            </div>
            );
  //    }
    }
}

export default connect(
    'weather, code',
    actions
)(withRouter(Sayonara));
