import React, { Component } from "react";
import { Link } from "react-router-dom";
import { withRouter } from "react-router-dom";
import {connect} from 'unistore/react'
import {actions} from '../store'
class ContentAll extends Component {
	render(){
    return (
		<div class="col-lg-4 mb-4 text-center">
			<div class="product-entry border">
				<Link to="/productdetail" class="prod-img" value={props.id} onClick={e => this.props.setId(e)}>
					<img src={props.imgurl1} class="img-fluid" alt="Free html5 bootstrap 4 template" value={props.id}/>
				</Link>
				<div class="desc">
					<h2><Link to="/productdetail" value={props.id} onClick={e => this.props.setId(e)}>{props.item_name}</Link></h2>
					<span class="price">Rp.{props.price}</span>
				</div>
			</div>
		</div>							
    );
}
}
export default connect(
	"isLogin",
	actions
)(withRouter(ContentAll));
