import React, { Component } from "react";
import "./assets/css/listticket.css";
import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../store";
import "../styles/App.css";
import IsiTicketList from "./IsiTicketList";
import Navbar from "./Navbar";

class TicketList extends Component {
  componentDidMount() {
    this.props.getData();
  }

  changeAsal = e => {
    this.props.doSet(e.target.value);
  };

  translate = e => {
    this.props.getTranslate();
  };

  estimasi_harga_batik = e => {
    this.props.getPrice(
      this.props.latitude_asal,
      this.props.longitude_asal,
      this.props.latitude_tujuan,
      this.props.longitude_tujuan
    );
    const price = Math.round(this.props.distance_in_km * 1586.726998492);
    return price;
  };

  estimasi_harga_citilink = e => {
    this.props.getPrice(
      this.props.latitude_asal,
      this.props.longitude_asal,
      this.props.latitude_tujuan,
      this.props.longitude_tujuan
    );
    const price = Math.round(this.props.distance_in_km * 1395.173453997);
    return price;
  };

  estimasi_harga_garuda = e => {
    this.props.getPrice(
      this.props.latitude_asal,
      this.props.longitude_asal,
      this.props.latitude_tujuan,
      this.props.longitude_tujuan
    );
    const price = Math.round(this.props.distance_in_km * 2419.306184012);
    return price;
  };

  estimasi_harga_lion = e => {
    this.props.getPrice(
      this.props.latitude_asal,
      this.props.longitude_asal,
      this.props.latitude_tujuan,
      this.props.longitude_tujuan
    );
    const price = Math.round(this.props.distance_in_km * 1271.49321267);
    return price;
  };

  estimasi_harga_sriwijaya = e => {
    this.props.getPrice(
      this.props.latitude_asal,
      this.props.longitude_asal,
      this.props.latitude_tujuan,
      this.props.longitude_tujuan
    );
    const price = Math.round(this.props.distance_in_km * 1736.04826546);
    return price;
  };

  estimasi_harga_lain = e => {
    this.props.getPrice(
      this.props.latitude_asal,
      this.props.longitude_asal,
      this.props.latitude_tujuan,
      this.props.longitude_tujuan
    );
    const price = Math.round(this.props.distance_in_km * 2000);
    return price;
  };

  correctList = flight_list => {
    return flight_list.arrival.iataCode === this.props.iata_tujuan;
  };

  render() {
    const filtered = this.props.flight_list.filter(this.correctList);
    return (
      <div>
        <Navbar />
        {filtered.map((item, key) => {
          return (
            <IsiTicketList
              departureIataCode={item.departure.iataCode}
              arrivalIataCode={item.arrival.iataCode}
              airline={item.airline.name}
              waktu_asal={item.departure.scheduledTime}
              waktu_tujuan={item.arrival.scheduledTime}
              estimasi_harga_batik={this.estimasi_harga_batik()}
              estimasi_harga_citilink={this.estimasi_harga_citilink()}
              estimasi_harga_garuda={this.estimasi_harga_garuda()}
              estimasi_harga_lion={this.estimasi_harga_lion()}
              estimasi_harga_sriwijaya={this.estimasi_harga_sriwijaya()}
              estimasi_harga_lain={this.estimasi_harga_lain()}
            />
          );
        })}
      </div>
    );
  }
}

export default connect(
  "is_login,airport_id_asal,airport_name_asal,nation_code_asal,latitude_asal,longitude_asal,airport_id_tujuan,airport_name_tujuan,nation_code_tujuan,latitude_tujuan,longitude_tujuan,iata_tujuan,range,weather,rate,new_sum,flight_list, word, distance_in_km, weather_code",
  actions
)(withRouter(TicketList));
