import React, { Component } from 'react';
import loadjs from 'loadjs';
import { Link } from "react-router-dom";
import Footer from '../components/Footer'
import BannerAll from '../components/BannerAll'
import ContentAll from '../components/ContentAll'



class AllProduct extends Component {

  componentWillMount() {
    loadjs(["js/jquery.min.js", 
    "../js/popper.min.js", 
    "../js/bootsrap.min.js", 
    "../js/jquery.easing.1.3.js", 
    "../js/jquery.waypoints.min.js",
    "../js/jquery.flexslider-min.js",
    "../js/owl.carousel.min.js",
    "../js/jquery.magnific-popup.min.js",
    "../js/magnific-popup-options.js",
    "../js/bootstrap-datepicker.js",
    "../js/jquery.stellar.min.js",
    "../js/main.js",
  ], function() {
    });
  };

	// componentDidMount = async () => {

	// }

  render() {
    return (
      <div>
        <BannerAll/>
        <div class="colorlib-product">
			<div class="container">
				<div class="row">
					<div class="col-lg-3 col-xl-3">
						<div class="row">
							<div class="col-sm-12">
								<div class="side border mb-1">
									<h3>Category</h3>
									<ul>
										<li><Link to="/category" value="casual">Casuals</Link></li>
										<li><Link to="/category" value="formal">Formal</Link></li>
										<li><Link to="/category" value="sport">Sports</Link></li>
										<li><Link to="/category" value="brogues">Brogues</Link></li>
										<li><Link to="/category" value="boots">Boots</Link></li>
										<li><Link to="/category" value="slipon">Slip On</Link></li>
									</ul>
								</div>
							</div>
						</div>
					</div>
					<div class="col-lg-9 col-xl-9">
						<div class="row row-pb-md">
            {all_product.map((item, key) => {
              return (
              <ContentAll key={key} imgurl1={item.imgurl1} item_name={item.item_name} price={item.price}/>
              )
              })}
              </div>
						<div class="row">
							<div class="col-md-12 text-center">
								<div class="block-27">
									<ul>
										<li><a href="#"><i class="ion-ios-arrow-back"></i></a></li>
										<li class="active"><span>1</span></li>
										<li><a href="#">2</a></li>
										<li><a href="#">3</a></li>
										<li><a href="#">4</a></li>
										<li><a href="#">5</a></li>
										<li><a href="#"><i class="ion-ios-arrow-forward"></i></a></li>
									</ul>
								</div>
							</div>
						</div>
					</div>
				</div>
			</div>
		</div>
    <Footer/>
  </div>
    );
  }
}

export default connect(
	"all_product",
	actions
)(withRouter(AllProduct));
