import React, { Component } from "react";
import Navbar from "./Navbar";
import "./assets/css/listticket.css";
import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../store";
import "../styles/App.css";

class Sayonara extends Component {
  render() {
    return (
      <div>
        <Navbar />
        <div className="card mb-3">
          {this.props.weather_code >= 200 && this.props.weather_code < 300 && (
            <div>
              <h3>Cuaca di kota tujuan anda saat ini</h3>
              <img className="card-img-top" src={require("../images/img/hujan-lebat.png")} alt="Card image cap"/>
              <div className="card-body">
                <h5 className="card-title">{this.props.weather}</h5>
                <p className="card-text">Jangan keluyuran nanti cakit...</p>
              </div>
            </div>
          )}
          {this.props.weather_code >= 300 && this.props.weather_code < 500 && (
            <div>
              <h3>Cuaca di kota tujuan anda saat ini</h3>
              <img className="card-img-top" src={require("../images/img/gerimis.png")} alt="Card image cap"/>
              <div className="card-body">
                <h5 className="card-title">{this.props.weather}</h5>
                <p className="card-text">
                  Gerimis-gerimis enaknya yang anget2.. h3h3
                </p>
              </div>
            </div>
          )}
          {this.props.weather_code >= 500 && this.props.weather_code < 700 && (
            <div>
              <h3>Cuaca di kota tujuan anda saat ini</h3>
              <img className="card-img-top" src={require("../images/img/hujan.png")} alt="Card image cap"/>
              <div className="card-body">
                <h5 className="card-title">{this.props.weather}</h5>
                <p className="card-text">Indomie enak nih..</p>
              </div>
            </div>
          )}
          {this.props.weather_code > 700 && (
            <div>
              <h3>Cuaca di kota tujuan anda saat ini</h3>
              <img className="card-img-top" src={require("../images/img/cerah.png")} alt="Card image cap"/>
              <div className="card-body">
                <h5 className="card-title">{this.props.weather}</h5>
                <p className="card-text">Jangan lupa cari yang seger-seger..</p>
              </div>
            </div>
          )}
        </div>
      </div>
    );
  }
}

export default connect(
  "weather, weather_code",
  actions
)(withRouter(Sayonara));
